from typing import List


def intersperse(numbers: List[int], delimeter: int) -> List[int]:
    """ Insert a number 'delimeter' between every two consecutive elements of input list `numbers'
    >>> intersperse([], 4)
    []
    >>> intersperse([1, 2, 3], 4)
    [1, 4, 2, 4, 3]
    """
    if len(numbers) < 2:
        return numbers
    else:
        return numbers[:-1] + [delimeter] + intersperse(numbers[-1:], delimeter)

def greatest_common_divisor(a: int, b: int) -> int:
    """ Return a greatest common divisor of two integers a and b
    >>> greatest_common_divisor(3, 5)
    1
    >>> greatest_common_divisor(25, 15)
    5
    """
    if b == 0:
        return abs(a)
    else:
        return greatest_common_divisor(b, a % b)

def gcd(a: int, b: int) -> int:
    return greatest_common_divisor(a, b)

def main():
    print(gcd(3,5))
    print(gcd(25,15))

if __name__ == "__main__":
    main()

def largest_divisor(n: int) -> int:
    """ For a given number n, find the largest number that divides n evenly, smaller than n
    >>> largest_divisor(15)
    5
    """
    for i in range(1, n+1):
        if n % i == 0:
            return i
    return n

def largest_divisor_iterative(n: int) -> int:
    """ For a given number n, find the largest number that divides n evenly, smaller than n
    >>> largest_divisor_iterative(15)
    5
    """
    max_divisor = 1
    for i in range(2, n+1):
        if n % i == 0:
            max_divisor = i
    return max_divisor

def largest_divisor_recursive(n: int) -> int:
    """ For a given number n, find the largest number that divides n evenly, smaller than n
    >>> largest_divisor_recursive(15)
    5
    """
    if n == 1:
        return n
    else:
        return largest_divisor_recursive(n//2)

def flip_case(string: str) -> str:
    """ For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
    >>> flip_case('Hello')
    'hELLO'
    """
    return ''.join(char.upper() if char.islower() else char.lower() for char in string)

def unique(l: list):
    """Return sorted unique elements in a list
    >>> unique([5, 3, 5, 2, 3, 3, 9, 0, 123])
    [0, 2, 3, 5, 9, 123]
    """
    return sorted(list(set(l)))

