def incr_dict(d: dict):
    """Return dictionary with values incremented by 1.
    >>> incr_dict({1: 2, 2: 3})
    {1: 3, 2: 4}
    >>> incr_dict({5: 3, 3: 5, 2: 3, 3: 3, 9: 0, 0: 123})
    {5: 4, 3: 6, 2: 4, 3: 4, 9: 1, 0: 124}
    """
    return {k: v + 1 for k, v in d.items()}

def incr_set(s: set):
