def monotonic(l: list):
    """Return True is list elements are monotonically increasing or decreasing.
    >>> monotonic([1, 2, 4, 20])
    True
    >>> monotonic([1, 20, 4, 10])
    False
    >>> monotonic([4, 1, 0, -10])
    True
    """
    if len(l) < 2:
        return True
    for i in range(1, len(l)):
        if l[i - 1] > l[i]:
            return False
    return True

def monotonic_sort(l: list):
    """Return sorted list elements monotonically increasing or decreasing."""
    if monotonic(l):
        return l
    else:
        return sorted(l)

def monotonic_sort_reverse(l: list):
    """Return sorted list elements monotonically decreasing."""
    if monotonic(l):
        return l[::-1]
    else:
        return sorted(l, reverse=True)

def largest_prime_factor(n: int):
    """Return the largest prime factor of n. Assume n > 1 and is not a prime.
    >>> largest_prime_factor(13195)
    29
    >>> largest_prime_factor(2048)
    2
    """
    if n < 2:
        raise ValueError("n must be greater than 1")
    if n % 2 == 0:
        return n // 2
    i = 3
    while i * i <= n:
        if n % i == 0:
            return n // i
        i += 2
    return n

