        return operand[0] * sum(operand[1:])
    elif operator == ['//']:
        return operand[0] // sum(operand[1:])
    elif operator == ['**']:
        return operand[0] ** sum(operand[1:])
    else:
        raise ValueError("operator must be one of +, -, *, //, **")
